import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState([]);

  // fetchData라는 이름을 정의하여 try-catch 문을 사용해 비동기작업 중 발생하는 에러를 잡아내고 콘솔에 메세지를 출력하는 것
  // 간접적으로 호출
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:8080/api/member", {
          withCredentials: true,
        });
        setData(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:8080/api/java", {
          withCredentials: true,
        });
        setData(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:8080/api/react", {
          withCredentials: true,
        });
        setData(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, []);

  /*
  직접적으로 한 번에 axios를 호출하는지 차이가 있을 뿐 위 코드와 아래 코드는 같은 것임.



  useEffect 안에서 직접 비동기 작업을 수행하고 간단하게 catch문을 사용하여 에러를 처리하고 콘솔에 에러 메세지를 출력

  useEffect(() => {
    axios
      // API 주소값을 가져옴.
      .get("http://localhost:8080/api/expression", { withCredentials: true })
      // response와 res는 같은 의미이며, 안에 변수값은 정해진 변수값은 없지만 되도록이면 res response를 사용하면 더 좋음 !
      .then((res) => {
        setData(res.data);
      })
      .catch((error) => {
        console.log("데이터없음", error);
      });
    // 처음에만 가져올 것이기 때문에 [] 공백을 넣어줌.
  }, []);
  */
  return (
    <div>
      <h1>API 호출 확인</h1>
      <ul>
        {data.map((member) => {
          <li key={member.id}>
            {member.membername} = {member.email}
          </li>;
        })}
      </ul>
    </div>
  );
}

export default App;
